package com.example.demo.environmentimport org.springframework.context.MessageSourceimport org.springframework.context.annotation.Beanimport org.springframework.context.annotation.Configurationimport org.springframework.context.annotation.Profileimport org.springframework.context.support.ReloadableResourceBundleMessageSource@Configurationclass TestConfiguration: BookRepository {    @Bean    @Profile("test")    fun bookRepository(): BookRepository {        println("test profile 적용된 BookRepository")        return TestBookRepository()    }    @Bean    fun messageSource(): MessageSource {        val messageSource = ReloadableResourceBundleMessageSource()        messageSource.setBasename("classpath:/messages")        messageSource.setDefaultEncoding("UTF-8")        messageSource.setCacheSeconds(3)        return messageSource    }}