package com.example.demo.evnetimport org.springframework.context.event.EventListenerimport org.springframework.core.Orderedimport org.springframework.core.annotation.Orderimport org.springframework.stereotype.Component@Componentclass AnotherHandler {    @EventListener    @Order(Ordered.HIGHEST_PRECEDENCE)    fun handle(myEvent: MyEvent) {        println(Thread.currentThread())        println("Another : ${myEvent.data}, ${myEvent.source}")    }}