package com.example.demo.evnetimport org.springframework.context.event.ContextRefreshedEventimport org.springframework.context.event.EventListenerimport org.springframework.scheduling.annotation.Asyncimport org.springframework.stereotype.Component@Componentclass MyEventHandler {    @EventListener    @Async    fun handle(myEvent: MyEvent) {        println(Thread.currentThread())        println("이벤트 받았다. 데이트는 ${myEvent.data}, ${myEvent.source}")    }    @EventListener    fun handle(event : ContextRefreshedEvent) {        println(Thread.currentThread())        println("ContextRefreshedEvent")    }}