package com.example.demo.h2import org.springframework.beans.factory.annotation.Autowiredimport org.springframework.boot.ApplicationArgumentsimport org.springframework.boot.ApplicationRunnerimport org.springframework.jdbc.core.JdbcTemplateimport org.springframework.stereotype.Componentimport javax.sql.DataSource@Componentclass H2Runner: ApplicationRunner {    @Autowired    private lateinit var dataSource: DataSource    @Autowired    private lateinit var jdbcTemplate: JdbcTemplate    override fun run(args: ApplicationArguments?) {        val connection = dataSource.connection        println("${dataSource.javaClass}")        println("h2 url ${connection.metaData.url}")        println("h2 username ${connection.metaData.userName}")        val statement = connection.createStatement()        val sql = "CREATE TABLE USER (ID INTEGER NOT NULL, name VARCHAR(255), PRIMARY KEY (id))"        statement.executeUpdate(sql)        jdbcTemplate.execute("INSERT INTO USER VALUES (1, 'chu')")    }}