package com.example.demo.messageSourceimport org.springframework.beans.factory.annotation.Autowiredimport org.springframework.boot.ApplicationArgumentsimport org.springframework.boot.ApplicationRunnerimport org.springframework.context.MessageSourceimport org.springframework.stereotype.Componentimport java.util.*@Componentclass MessageAppRunner : ApplicationRunner {    @Autowired    lateinit var messageSource: MessageSource    override fun run(args: ApplicationArguments?) {        println(messageSource.javaClass)        println(Locale.getDefault())        println(messageSource.getMessage("greeting", arrayOf("chu"), Locale.KOREA))        println(messageSource.getMessage("greeting", arrayOf("chu"), Locale.US))//        while (true) {//            println(messageSource.getMessage("greeting", arrayOf("chu"), Locale.KOREA))//            println(messageSource.getMessage("greeting", arrayOf("chu"), Locale.US))//            Thread.sleep(1001)//        }    }}