package com.example.demo.messageSourceimport org.springframework.context.MessageSourceimport org.springframework.context.annotation.Beanimport org.springframework.context.annotation.Configurationimport org.springframework.context.annotation.PropertySourceimport org.springframework.context.annotation.PropertySourcesimport org.springframework.context.support.ReloadableResourceBundleMessageSource@PropertySources(    PropertySource("classpath:/messages_en_US.properties"),    PropertySource("classpath:/messages_ko_KR.properties"))@Configurationclass MessageConfiguration {    @Bean    fun messageSource(): MessageSource {        val messageSource = ReloadableResourceBundleMessageSource()        messageSource.setBasename("classpath:/messages")        messageSource.setDefaultEncoding("UTF-8")        messageSource.setCacheSeconds(3)        return messageSource    }}