package com.example.demo.redisimport org.springframework.beans.factory.annotation.Autowiredimport org.springframework.boot.ApplicationArgumentsimport org.springframework.boot.ApplicationRunnerimport org.springframework.data.redis.core.StringRedisTemplateimport org.springframework.stereotype.Component@Componentclass RedisRunner: ApplicationRunner {    @Autowired    private lateinit var stringRedisTemplate: StringRedisTemplate    @Autowired    private lateinit var redisAccountRepository: RedisAccountRepository    override fun run(args: ApplicationArguments?) {        val values = stringRedisTemplate.opsForValue()        values.set("chu1", "1")        values.set("chu2", "2")        values.set("chu3", "2")        val account = RedisAccount(0, "chu", "chu@naver.com")        redisAccountRepository.save(account)        val byId = redisAccountRepository.findById(account.id)        println("redis username : ${byId.get().username}")        println("redis email : ${byId.get().email}")    }}