package com.example.demo.resourceimport org.springframework.beans.factory.annotation.Autowiredimport org.springframework.boot.ApplicationArgumentsimport org.springframework.boot.ApplicationRunnerimport org.springframework.core.io.ResourceLoaderimport org.springframework.stereotype.Componentimport java.nio.file.Filesimport java.nio.file.Path@Componentclass ResourceAppRunner : ApplicationRunner {    @Autowired    lateinit var resourceLoader: ResourceLoader    override fun run(args: ApplicationArguments?) {        val resource = resourceLoader.getResource("classpath:test.txt")        println("resource.exists = ${resource.exists()}")        println("resource.description = ${resource.description}")        println("file name = ${Files.readString(Path.of(resource.uri))}")    }}