package com.example.demo.scopeimport org.springframework.beans.factory.annotation.Autowiredimport org.springframework.boot.ApplicationArgumentsimport org.springframework.boot.ApplicationRunnerimport org.springframework.context.ApplicationContextimport org.springframework.stereotype.Component@Componentclass ScopeRunner : ApplicationRunner {    @Autowired    private lateinit var ctx: ApplicationContext    override fun run(args: ApplicationArguments?) {        println("single")        println(ctx.getBean(Single::class.java))        println(ctx.getBean(Single::class.java))        println(ctx.getBean(Single::class.java))        println("proto")        println(ctx.getBean(Proto::class.java))        println(ctx.getBean(Proto::class.java))        println(ctx.getBean(Proto::class.java))        println("proto by single => problem")        println(ctx.getBean(Single::class.java).proto)        println(ctx.getBean(Single::class.java).proto)        println(ctx.getBean(Single::class.java).proto)    }}