package com.example.demo.scopeimport org.springframework.beans.factory.annotation.Autowiredimport org.springframework.boot.ApplicationArgumentsimport org.springframework.boot.ApplicationRunnerimport org.springframework.stereotype.Component@Componentclass ScopeRunner : ApplicationRunner {    @Autowired    private lateinit var single1: Single    @Autowired    private lateinit var single2: Single    @Autowired    private lateinit var single3: Single    @Autowired    private lateinit var proto1: Proto    @Autowired    private lateinit var proto2: Proto    @Autowired    private lateinit var proto3: Proto    override fun run(args: ApplicationArguments?) {        println("single1 = $single1")        println("single2 = $single2")        println("single3 = $single3")        println("proto1 = $proto1")        println("proto2 = $proto2")        println("proto3 = $proto3")    }}