package com.example.demo.securityimport org.springframework.beans.factory.annotation.Autowiredimport org.springframework.boot.ApplicationArgumentsimport org.springframework.boot.ApplicationRunnerimport org.springframework.stereotype.Component@Componentclass SecurityRunner: ApplicationRunner {    @Autowired    private lateinit var accountService: AccountService    override fun run(args: ApplicationArguments?) {         val account = accountService.createAccount("chu", "123")        println("account = ${account.username}, ${account.password}")    }}