package com.example.demo.accountimport org.assertj.core.api.Assertions.assertThatimport org.junit.jupiter.api.Testimport org.springframework.beans.factory.annotation.Autowiredimport org.springframework.boot.test.autoconfigure.orm.jpa.DataJpaTestimport org.springframework.jdbc.core.JdbcTemplateimport javax.sql.DataSource@DataJpaTestclass AccountRepositoryTest {    @Autowired    private lateinit var dataSource: DataSource    @Autowired    private lateinit var jdbcTemplate: JdbcTemplate    @Autowired    private lateinit var accountRepository: AccountRepository    @Test    fun `테스트`() {        val account = Account(0, "chu", "pass")        val newAccount = accountRepository.save(account)        assertThat(newAccount).isNotNull        val existingAccount = accountRepository.findByUsername(newAccount.username)        assertThat(existingAccount).isNotNull    }}