package com.example.demo.evnetimport org.junit.jupiter.api.Testimport org.springframework.beans.factory.annotation.Autowiredimport org.springframework.boot.test.context.SpringBootTestimport org.springframework.context.ApplicationEventPublisherimport org.springframework.context.event.ContextRefreshedEventimport org.springframework.context.event.EventListener@SpringBootTestinternal class MyEventHandlerTest{    @Autowired    private lateinit var publisher: ApplicationEventPublisher    @Test    fun `event 를 발생시키고 핸들링 한다`() {        publisher.publishEvent(MyEvent(this,100))    }    @EventListener    fun handle(event : ContextRefreshedEvent) {        println(Thread.currentThread())        println("ContextRefreshedEvent")    }}